import React, { useEffect, useState } from 'react';

const MyRequests = () => {
  const [requests, setRequests] = useState([]);
  const [loading, setLoading] = useState(true);

  const userId = localStorage.getItem('userId');

  useEffect(() => {
    const fetchRequests = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/help-requests/${userId}`);
        const data = await response.json();

        if (data.success) {
          setRequests(data.helpRequests);
        } else {
          alert('Failed to fetch your help requests');
        }
      } catch (error) {
        console.error('Error fetching help requests:', error);
        alert('Something went wrong. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    if (userId) {
      fetchRequests();
    } else {
      alert('User not logged in.');
      setLoading(false);
    }
  }, [userId]);

  return (
    <div className="container mt-5">
      <h2>üìã My Help Requests</h2>
      {loading ? (
        <p>Loading...</p>
      ) : requests.length > 0 ? (
        <ul className="list-group">
          {requests.map((req) => (
            <li key={req.id} className="list-group-item">
              <strong>üìç Location:</strong> {req.location}<br />
              <strong>üõ† Issue:</strong> {req.issue}
            </li>
          ))}
        </ul>
      ) : (
        <p>You have not submitted any help requests yet.</p>
      )}
    </div>
  );
};

export default MyRequests;
