import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const ProviderDashboard = () => {
  const [helpRequests, setHelpRequests] = useState([]);

  useEffect(() => {
    const fetchRequests = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/requests');
        const data = await response.json();

        if (response.ok) {
          setHelpRequests(data);
        } else {
          console.error('Failed to fetch help requests');
        }
      } catch (error) {
        console.error('Error fetching help requests:', error);
      }
    };

    fetchRequests();
  }, []);

  return (
    <div className="container">
      <h2>Service Provider Dashboard</h2>
      <p className="text-center">Active help requests:</p>

      {helpRequests.length > 0 ? (
        <ul className="list-group">
          {helpRequests.map((req) => (
            <li key={req.id} className="list-group-item">
              üìç <strong>{req.location}</strong> ‚Äì {req.issue}
            </li>
          ))}
        </ul>
      ) : (
        <p className="text-muted text-center">No active requests at the moment.</p>
      )}

      <Link to="/" className="btn btn-secondary mt-3">Logout</Link>
    </div>
  );
};

export default ProviderDashboard;
