import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Dashboard.css';

const Dashboard = () => {
  const navigate = useNavigate();
  const userEmail = localStorage.getItem('userEmail'); // Optional: if saved during login
  const userId = localStorage.getItem('userId'); // Assuming userId is saved during login
  const [helpRequests, setHelpRequests] = useState([]);
  const [loading, setLoading] = useState(true); // Loading state for help requests

  // Fetch help requests for the user
  useEffect(() => {
    const fetchHelpRequests = async () => {
      const token = localStorage.getItem('token'); // If you're using a token for authentication

      try {
        const response = await fetch(`/api/help-requests/${userId}`, {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        });
        const data = await response.json();

        if (response.ok) {
          setHelpRequests(data.helpRequests); // Set the state with fetched help requests
        } else {
          console.error('Error fetching help requests:', data.message);
        }
      } catch (error) {
        console.error('An error occurred while fetching help requests:', error);
      } finally {
        setLoading(false); // Stop loading when the request is completed
      }
    };

    if (userId) {
      fetchHelpRequests();
    }
  }, [userId]);

  const handleLogout = () => {
    localStorage.clear(); // Clear all stored data
    navigate('/');        // Redirect to login
  };

  return (
    <div className="dashboard-wrapper">
      <div className="dashboard-card">
        <h2 className="dashboard-title">ðŸš— User Dashboard</h2>
        <p className="dashboard-text">
          Welcome{userEmail ? `, ${userEmail}` : ''}! Ready to request assistance?
        </p>

        {/* Displaying Help Requests */}
        {loading ? (
          <p>Loading your requests...</p>
        ) : (
          <div className="help-requests-section">
            <h3>Your Help Requests</h3>
            {helpRequests.length > 0 ? (
              <ul>
                {helpRequests.map((request) => (
                  <li key={request.id}>
                    <p><strong>Request ID:</strong> {request.id}</p>
                    <p><strong>Status:</strong> {request.status}</p>
                    <p><strong>Details:</strong> {request.details}</p>
                    {/* Add other details from the help request here */}
                  </li>
                ))}
              </ul>
            ) : (
              <p>You have no active help requests.</p>
            )}
          </div>
        )}

        <div className="dashboard-buttons">
          <Link to="/request-help" className="dashboard-btn primary">Request Help</Link>
          <Link to="/my-requests" className="dashboard-btn primary">My Requests</Link>
          <button onClick={handleLogout} className="dashboard-btn secondary">Logout</button>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
