import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './ProviderDashboard.css'; // Make sure this file exists

const ProviderDashboard = () => {
  const [helpRequests, setHelpRequests] = useState([]);

  useEffect(() => {
    const fetchRequests = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/requests');
        const data = await response.json();

        if (response.ok) {
          setHelpRequests(data);
        } else {
          console.error('Failed to fetch help requests');
        }
      } catch (error) {
        console.error('Error fetching help requests:', error);
      }
    };

    fetchRequests();
  }, []);

  return (
    <div className="provider-dashboard-wrapper">
      <div className="provider-dashboard-card">
        <h2 className="provider-title">üîß Service Provider Dashboard</h2>
        <p className="provider-subtitle">Active help requests:</p>

        {helpRequests.length > 0 ? (
          <ul className="request-list">
            {helpRequests.map((req) => (
              <li key={req.id} className="request-item">
                <strong>üìç {req.location}</strong> ‚Äî {req.issue}
              </li>
            ))}
          </ul>
        ) : (
          <p className="no-requests">No active requests at the moment.</p>
        )}

        <Link to="/" className="provider-logout-btn">Logout</Link>
      </div>
    </div>
  );
};

export default ProviderDashboard;
