import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const RequestHelp = () => {
  const [location, setLocation] = useState('');
  const [issue, setIssue] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Help requested at ${location} for issue: ${issue}`);
  };

  return (
    <div className="container">
      <h2>Request Help</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label>Location</label>
          <input type="text" className="form-control" value={location}
            onChange={(e) => setLocation(e.target.value)} required />
        </div>
        <div className="mb-3">
          <label>Issue</label>
          <textarea className="form-control" rows="4" value={issue}
            onChange={(e) => setIssue(e.target.value)} required />
        </div>
        <button type="submit" className="btn btn-danger w-100">Submit Request</button>
      </form>
      <Link to="/dashboard" className="btn btn-secondary mt-3">Back to Dashboard</Link>
    </div>
  );
};

export default RequestHelp;
