import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const RequestHelp = () => {
  const [details, setDetails] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const userId = localStorage.getItem('userId'); // Get the logged-in user's ID
    const token = localStorage.getItem('token');   // Token if you're using authentication

    try {
      const response = await fetch('/api/help-request', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ userId, details }),
      });
      const data = await response.json();

      if (response.ok) {
        alert('Help request created successfully!');
        navigate('/my-requests'); // Redirect to the "My Requests" page after successful submission
      } else {
        alert('Error creating help request: ' + data.message);
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Something went wrong!');
    }
  };

  return (
    <div className="request-help-wrapper">
      <h2>Request Help</h2>
      <form onSubmit={handleSubmit}>
        <textarea
          value={details}
          onChange={(e) => setDetails(e.target.value)}
          placeholder="Describe your issue..."
          required
        />
        <button type="submit">Submit Request</button>
      </form>
    </div>
  );
};

export default RequestHelp;
