// components/dashboard/UserDashboard.js
import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Dashboard.css';

const UserDashboard = () => {
  const navigate = useNavigate();
  const userEmail = localStorage.getItem('userEmail'); // Optional: if saved during login
  const userId = localStorage.getItem('userId'); // Assuming you saved the user ID on login
  const [helpRequests, setHelpRequests] = useState([]);

  // Fetch help requests from the backend
  useEffect(() => {
    if (userId) {
      fetch(`http://localhost:5000/api/help-requests/${userId}`)
        .then(response => response.json())
        .then(data => {
          if (data.success) {
            setHelpRequests(data.helpRequests);
          }
        })
        .catch(error => console.error('Error fetching help requests:', error));
    }
  }, [userId]);

  const handleLogout = () => {
    localStorage.clear(); // Clear all stored data
    navigate('/');        // Redirect to login
  };

  return (
    <div className="dashboard-wrapper">
      <div className="dashboard-card">
        <h2 className="dashboard-title">ðŸš— User Dashboard</h2>
        <p className="dashboard-text">
          Welcome{userEmail ? `, ${userEmail}` : ''}! Ready to request assistance?
        </p>

        <div className="dashboard-buttons">
          <Link to="/request-help" className="dashboard-btn primary">Request Help</Link>
          <Link to="/my-requests" className="dashboard-btn primary">My Requests</Link>
          <button onClick={handleLogout} className="dashboard-btn secondary">Logout</button>
        </div>

        {/* Display Help Requests */}
        <div className="help-requests-section">
          <h3>My Help Requests</h3>
          {helpRequests.length > 0 ? (
            <ul>
              {helpRequests.map(request => (
                <li key={request.id}>
                  <p>Request ID: {request.id}</p>
                  <p>Status: {request.status}</p>
                  {/* You can display more details about the request */}
                </li>
              ))}
            </ul>
          ) : (
            <p>No help requests found.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default UserDashboard;
