import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const RequestHelp = () => {
  const [location, setLocation] = useState('');
  const [issue, setIssue] = useState('');
  const [userId, setUserId] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const storedUserId = localStorage.getItem('userId');
    if (storedUserId) {
      setUserId(storedUserId);
    } else {
      alert('You must be logged in to request help.');
      navigate('/');
    }
  }, [navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/api/help-request', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId, issue, location }),
      });

      const data = await response.json();

      if (response.ok) {
        alert('Help request submitted successfully!');
        setLocation('');
        setIssue('');
      } else {
        alert(data.message || 'Failed to submit help request');
      }
    } catch (error) {
      console.error('Error submitting help request:', error);
      alert('Something went wrong. Please try again.');
    }
  };

  return (
    <div className="container">
      <h2>Request Help</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label>Location</label>
          <input
            type="text"
            className="form-control"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <label>Issue</label>
          <textarea
            className="form-control"
            rows="4"
            value={issue}
            onChange={(e) => setIssue(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="btn btn-danger w-100">Submit Request</button>
      </form>

      {/* âœ… Corrected link path */}
      <Link to="/user-dashboard" className="btn btn-secondary mt-3">Back to Dashboard</Link>
    </div>
  );
};

export default RequestHelp;
