// components/provider/ProviderDashboard.js
import React, { useEffect, useState } from 'react';

const ProviderDashboard = () => {
  const [pendingRequests, setPendingRequests] = useState([]);
  const [loading, setLoading] = useState(true);

  const providerId = localStorage.getItem('userId');

  useEffect(() => {
    const fetchPendingRequests = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/help-requests/pending');
        const data = await response.json();
        if (data.success) {
          setPendingRequests(data.helpRequests);
        } else {
          alert('‚ùå Failed to fetch pending help requests.');
        }
      } catch (error) {
        console.error('Error fetching pending requests:', error);
        alert('‚ö†Ô∏è Server error. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchPendingRequests();
  }, []);

  const acceptRequest = async (requestId) => {
    if (!providerId) {
      alert('üîí Provider not logged in.');
      return;
    }

    try {
      const response = await fetch(`http://localhost:5000/api/accept-help/${requestId}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ providerId }),
      });

      const data = await response.json();

      if (data.success) {
        alert('‚úÖ Request accepted!');
        setPendingRequests((prev) => prev.filter((req) => req.id !== requestId));
      } else {
        alert(data.message || '‚ùå Failed to accept request.');
      }
    } catch (error) {
      console.error('Error accepting request:', error);
      alert('‚ö†Ô∏è Error processing request.');
    }
  };

  return (
    <div className="container mt-5" style={{ maxWidth: '700px' }}>
      <h2 className="mb-4">üõ† Pending Help Requests</h2>
      {loading ? (
        <p>‚è≥ Loading...</p>
      ) : pendingRequests.length > 0 ? (
        <ul className="list-group shadow">
          {pendingRequests.map((req) => (
            <li key={req.id} className="list-group-item mb-3 rounded">
              <strong>üÜî Request ID:</strong> {req.id} <br />
              <strong>üìç Location:</strong> {req.location || 'Not specified'}<br />
              <strong>üõ† Issue:</strong> {req.issue || req.details}<br />
              <button
                className="btn btn-success mt-2"
                onClick={() => acceptRequest(req.id)}
              >
                ‚úÖ Accept Help
              </button>
            </li>
          ))}
        </ul>
      ) : (
        <p>üéâ No pending requests right now.</p>
      )}
    </div>
  );
};

export default ProviderDashboard;
