import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const RequestHelp = () => {
  const [location, setLocation] = useState('');
  const [issue, setIssue] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Help requested at ${location} for issue: ${issue}`);
    // Later we'll connect this to backend
  };

  return (
    <div className="container mt-5">
      <h2>Request Roadside Assistance</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label>Location</label>
          <input
            type="text"
            className="form-control"
            placeholder="Enter your location"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <label>Issue</label>
          <textarea
            className="form-control"
            placeholder="Describe your vehicle issue"
            value={issue}
            onChange={(e) => setIssue(e.target.value)}
            required
          ></textarea>
        </div>
        <button type="submit" className="btn btn-danger">Submit Help Request</button>
      </form>
      <Link to="/dashboard" className="btn btn-secondary mt-3">Back to Dashboard</Link>
    </div>
  );
};

export default RequestHelp;
