import React, { useEffect, useState } from 'react';

const MyRequests = () => {
  const [requests, setRequests] = useState([]);
  const [loading, setLoading] = useState(true);

  const userId = localStorage.getItem('userId');

  useEffect(() => {
    const fetchRequests = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/my-requests/${userId}`);
        const data = await response.json();
        setRequests(data);
      } catch (error) {
        console.error('Error fetching help requests:', error);
        alert('‚ö†Ô∏è Something went wrong. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    if (userId) {
      fetchRequests();
    } else {
      alert('üîí User not logged in.');
      setLoading(false);
    }
  }, [userId]);

  return (
    <div className="container mt-5" style={{ maxWidth: '700px' }}>
      <h2 className="mb-4">üìã My Help Requests</h2>
      {loading ? (
        <p>‚è≥ Loading...</p>
      ) : requests.length > 0 ? (
        <ul className="list-group shadow">
          {requests.map((req) => (
            <li key={req.id} className="list-group-item mb-3 rounded">
              <strong>üÜî Request ID:</strong> {req.id} <br />
              <strong>üìç Location:</strong> {req.location || 'Not specified'}<br />
              <strong>üõ† Issue:</strong> {req.details}<br />
              <strong>üìä Status:</strong> {req.status}
            </li>
          ))}
        </ul>
      ) : (
        <p>üôÅ You have not submitted any help requests yet.</p>
      )}
    </div>
  );
};

export default MyRequests;
